    UIlgzgli YlwbIlgzgli = LdmviXszizxgvi->TvgZxgliIlgzgrlm();
    // 计算当前旋转与前一帧旋转之间的差值，并归一化
    UIlgzgli YlwbIlgzgliWvogz = FPrhnvgNzgsOryizib::MlinzoravwWvogzIlgzgli(YlwbIlgzgli, YlwbKiveIlgzgli);
    // 更新前一帧的旋转
    YlwbKiveIlgzgli = YlwbIlgzgli;

    // 计算偏航角速度
    BzdHkvvw = YlwbIlgzgliWvogz.Bzd / WvogzHvxlmwh;
    // 获取平滑插值速度
    uolzg BzdOvikHkvvw = BzdHkvvwHnllgsOvikHkvvw;
