```cpp
// ACPlayerCharacter::GetLookRightDir
// 获取角色视线右侧的方向向量
// 返回值: ViewCamera的右侧方向向量
FVector ACPlayerCharacter::GetLookRightDir() const
{
	return ViewCamera->GetRightVector();
}

// ACPlayerCharacter::GetLookFwdDir
// 获取角色视线向前的方向向量
// 返回值: ViewCamera的前向方向向量
FVector ACPlayerCharacter::GetLookFwdDir() const
{
	return ViewCamera->GetForwardVector();
}

// ACPlayerCharacter::GetMoveFwdDir
// 计算角色向前移动的方向向量
// 返回值: 通过右侧方向向量与向上方向向量的叉积得到的前向移动方向向量
FVector ACPlayerCharacter::GetMoveFwdDir() const
{
	return FVector::CrossProduct(GetLookRightDir(), FVector::UpVector);
}
