// 幻雨喜欢小猫咪

#pragma once

#include "CoreMinimal.h"
#include "Animation/AnimInstance.h"
#include "CAnimInstance.generated.h"

/**
 * 
 */
UCLASS()
class UCAnimInstance : public UAnimInstance
{
	GENERATED_BODY()
public:
	/**
	 * 动画实例原生初始化覆盖点
	 * 用于执行动画实例的初始化逻辑，在实例创建时调用
	 */
	virtual void NativeInitializeAnimation() override;

	/**
	 * 动画实例原生更新覆盖点
	 * 主要用于在游戏线程中收集动画数据，实际计算建议放在 NativeThreadSafeUpdateAnimation 中
	 * 
	 * @param DeltaSeconds 当前帧与上一帧之间的时间间隔（秒）
	 */
	virtual void NativeUpdateAnimation(float DeltaSeconds) override;

	/**
	 * 动画实例线程安全更新覆盖点
	 * 在工作线程中执行，适用于链接动画实例的预处理计算
	 * 仅当宿主节点相关时才会调用
	 * 
	 * @param DeltaSeconds 当前帧与上一帧之间的时间间隔（秒）
	 */
	virtual void NativeThreadSafeUpdateAnimation(float DeltaSeconds) override;


private:
	UPROPERTY()
	TObjectPtr<class ACharacter> OwnerCharacter;

	UPROPERTY()
	TObjectPtr<class UCharacterMovementComponent> OwnerMovementComponent;
};

option.value:
```cpp
// CAnimInstance.cpp

// 检查拥有者角色是否存在
if (OwnerCharacter)
{
    // 获取拥有者角色的速度向量
    FVector Velocity = OwnerCharacter->GetVelocity();
    // 计算速度的大小
    Speed = Velocity.Length();

    // 获取拥有者角色的当前旋转
    FRotator BodyRotator = OwnerCharacter->GetActorRotation();
    // 计算当前旋转与前一帧旋转之间的差值，并归一化
    FRotator BodyRotatorDelta = UKismetMathLibrary::NormalizedDeltaRotator(BodyRotator, BodyPrevRotator);
    // 更新前一帧的旋转
    BodyPrevRotator = BodyRotator;

    // 计算偏航角速度
    YawSpeed = BodyRotatorDelta.Yaw / DeltaSeconds;
    // 获取平滑插值速度
    float YawLerpSpeed = YawSpeedSmoothLerpSpeed;
    // 如果偏航角速度为0，则使用特定的速度进行平滑插值
    if (YawSpeed == 0)
    {
        YawLerpSpeed = YawSpeedLerpToZeroSpeed;
    }
    // 使用插值函数平滑偏航角速度
    SmoothedYawSpeed = UKismetMathLibrary::FInterpTo(SmoothedYawSpeed, YawSpeed, DeltaSeconds, YawLerpSpeed);
}
```

