```cpp
// CAnimInstance.h

// YawSpeed: 表示当前帧的偏航角速度
// 用于计算角色在水平面上的旋转速度
float YawSpeed;

// SmoothedYawSpeed: 表示平滑后的偏航角速度
// 用于在多个帧之间平滑偏航角速度，以减少抖动
float SmoothedYawSpeed;

// YawSpeedSmoothLerpSpeed: 表示平滑插值的速度
// 控制偏航角速度从当前值平滑过渡到零的速度
UPROPERTY(EditAnywhere, Category = "Animation")
float YawSpeedSmoothLerpSpeed = 1.f;

// YawSpeedLerpToZeroSpeed: 表示当偏航角速度降到零时使用的插值速度
// 用于控制偏航角速度从当前值平滑过渡到零的加速度
UPROPERTY(EditAnywhere, Category = "Animation")
float YawSpeedLerpToZeroSpeed = 30.f;

// BodyPrevRotator: 表示前一帧的角色旋转
// 用于计算当前帧的旋转变化量
FRotator BodyPrevRotator;
```

