```cpp
// CPlayerController.h

// OnPossess: 当玩家控制器开始控制一个Pawn时调用，该函数仅在服务器中调用
// 参数:
//   NewPawn: 被控制的Pawn对象，玩家控制器将通过这个对象来控制游戏逻辑
virtual void OnPossess(APawn* NewPawn) override;

// AcknowledgePossession: 当玩家控制器确认接管一个Pawn时调用，该函数也仅在服务器中调用
// 参数:
//   P: 被控制的Pawn对象，玩家控制器通过这个对象来确认接管
virtual void AcknowledgePossession(class APawn* P) override;
```

