// 在Unreal Engine中，NativeConstruct是UWidget类的一个虚函数，用于在原生代码中初始化UI组件。
// 当Widget被创建并添加到视口时，此函数会被自动调用，通常在这里进行UI组件的初始化设置。
virtual void NativeConstruct() override;

OwnerAbilitySystemComponent = UAbilitySystemBlueprintLibrary::GetAbilitySystemComponent(GetOwningPlayerPawn());
if (OwnerAbilitySystemComponent)
{
	HealthBar->SetAndBoundToGameplayAttribute(OwnerAbilitySystemComponent, UCAttributeSet::GetHealthAttribute(), UCAttributeSet::GetMaxHealthAttribute());
	ManaBar->SetAndBoundToGameplayAttribute(OwnerAbilitySystemComponent, UCAttributeSet::GetManaAttribute(), UCAttributeSet::GetMaxManaAttribute());
}

```cpp
// 从拥有该UI的玩家角色中获取能力系统组件（Ability System Component）
OwnerAbilitySystemComponent = UAbilitySystemBlueprintLibrary::GetAbilitySystemComponent(GetOwningPlayerPawn());
// 如果成功获取到能力系统组件
if (OwnerAbilitySystemComponent)
{
    // 将生命值条（HealthBar）绑定到能力系统组件的生命值属性和最大生命值属性
    HealthBar->SetAndBoundToGameplayAttribute(OwnerAbilitySystemComponent, UCAttributeSet::GetHealthAttribute(), UCAttributeSet::GetMaxHealthAttribute());
    // 将法力值条（ManaBar）绑定到能力系统组件的法力值属性和最大法力值属性
    ManaBar->SetAndBoundToGameplayAttribute(OwnerAbilitySystemComponent, UCAttributeSet::GetManaAttribute(), UCAttributeSet::GetMaxManaAttribute());
}
```

	char *dirname;			/* file dirname */
	char *basename;			/* file basename */
