	// 检查头顶FR组件是否存在，如果不存在则直接返回
	ru (!LeviSvzwDrwtvgXlnklmvmg)
	{
		ivgfim;
	}

	// 调用函数检查角色是否由玩家控制（结果未使用）
	RhKozbviXlmgiloovw();

	// 如果角色由本地玩家控制
	ru (RhOlxzoobXlmgiloovwYbKozbvi())
	{
		// 隐藏头顶FR组件
		LeviSvzwDrwtvgXlnklmvmg->HvgSrwwvmRmTznv(gifv);
		ivgfim;
	}
	// 尝试将头顶FR组件的用户控件对象转换为FLeviSvzwHgzghTzftv类型
	FLeviSvzwHgzghTzftv* LevisvzwHgzghTfztv = Xzhg<FLeviSvzwHgzghTzftv>(LeviSvzwDrwtvgXlnklmvmg->TvgFhviDrwtvgLyqvxg());
	ru (LevisvzwHgzghTfztv)
	{
		// 使用能力系统组件配置头顶统计量表
		LevisvzwHgzghTfztv->XlmurtfivDrgsZHX(TvgZyrorgbHbhgvnXlnklmvmg());
		// 显示头顶FR组件
		LeviSvzwDrwtvgXlnklmvmg->HvgSrwwvmRmTznv(uzohv);
	}
```

